import Doc, { getPosts } from "@/lib/posts";

// throws a 404 error if the dynamic route was not generated by Next.js
export const dynamicParams = false;

export function generateStaticParams() {
  return getPosts().map((post) => ({
    slug: post.replace(/\..*$/, ""),
  }));
}

export default function Post({ params }: any) {
  const post = new Doc(params.slug);
  return (
    <>
      <header>
        <h1 className="post__title">{post.title}</h1>
        {post.hasSubtitle && (
          <h2 className="post__subtitle">{post.subtitle}</h2>
        )}
      </header>

      <aside className="post__metadata">
        <span>{post.date}</span>
      </aside>

      <main
        className="post__content"
        dangerouslySetInnerHTML={{ __html: post.content }}
      />
    </>
  );
}
